import React, { useState, useEffect } from 'react';
import Navbar from './components/Navbar';
import Panel from './components/Panel';
import Button from './components/Button';
import MoodCard from './components/MoodCard';
import MealCard from './components/MealCard';

const App = () => {
  const [selectedMood, setSelectedMood] = useState(null);
  const [meals, setMeals] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const moods = [
    { emoji: 'ðŸ˜Š', mood: 'Happy', description: 'Light & Fresh', category: 'Seafood' },
    { emoji: 'ðŸ˜Œ', mood: 'Relaxed', description: 'Comfort Food', category: 'Pasta' },
    { emoji: 'ðŸ˜´', mood: 'Tired', description: 'Quick & Easy', category: 'Chicken' },
    { emoji: 'ðŸŽ‰', mood: 'Excited', description: 'Special Treats', category: 'Dessert' },
    { emoji: 'ðŸ¤”', mood: 'Adventurous', description: 'Try Something New', category: 'Vegetarian' },
    { emoji: 'â¤ï¸', mood: 'Romantic', description: 'Elegant Dining', category: 'Lamb' }
  ];

  const fetchMeals = async (category) => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(
        `https://www.themealdb.com/api/json/v1/1/filter.php?c=${category}`
      );
      const data = await response.json();

      if (data.meals) {
        // Get detailed info for first 3 meals
        const detailedMeals = await Promise.all(
          data.meals.slice(0, 3).map(async (meal) => {
            const detailResponse = await fetch(
              `https://www.themealdb.com/api/json/v1/1/lookup.php?i=${meal.idMeal}`
            );
            const detailData = await detailResponse.json();
            return detailData.meals[0];
          })
        );
        setMeals(detailedMeals);} else {
        setMeals([]);
      }
    } catch (err) {
      setError('Failed to fetch meals. Please try again.');
      setMeals([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (selectedMood) {
      fetchMeals(selectedMood.category);
    }
  }, [selectedMood]);

  const handleMoodSelect = (mood) => {
    setSelectedMood(mood);
  };

  const handleReset = () => {
    setSelectedMood(null);
    setMeals([]);
    setError(null);
  };

  return (
    <div className="min-h-screen bg-orange-50">
      <Navbar />

      <div className="container mx-auto px-4 py-8">
        <Panel title="How are you feeling today?" className="mb-8">
          <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-6 gap-4">
            {moods.map((mood) => (
              <MoodCard
                key={mood.mood}
                emoji={mood.emoji}
                mood={mood.mood}
                description={mood.description}
                onClick={() => handleMoodSelect(mood)}
                isSelected={selectedMood?.mood === mood.mood}
              />
            ))}
          </div>
        </Panel>

        {selectedMood && (
          <Panel className="mb-8">
            <div className="flex justify-between items-center mb-6">
              <Button onClick={handleReset} variant="secondary">
                Choose Another Mood
              </Button>
            </div>
          </Panel>
        )}

        {!selectedMood && (
          <div className="text-center py-12">
            <p className="text-gray-600 text-lg">
              ðŸ‘† Select a mood above to discover amazing meals!
            </p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
