import React, {useState, useEffect} from 'react'
import TrackCard from '../components/TrackCard'
import {getRecommendations} from '../services/spotify'

const PlaylistPage = ({selectedMood, favorites, setFavorites}) => {
  const [tracks, setTracks] = useState([])
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)

  useEffect(() => {
    if (selectedMood) {
      fetchTracks()
    }
  }, [selectedMood])

  const fetchTracks = async () => {
    setLoading(true)
    setError(null)

    try {
      const data = await getRecommendations(selectedMood.genres, 20)
      setTracks(data)
    } catch (err) {
      setError('Failed to load music. Please try again.')
      console.error('Error fetching tracks:', err)
    }

    setLoading(false)
  }

  const handleFavorite = (track) => {
    const isFavorite = favorites.find(fav => fav.id === track.id)

    if (isFavorite) {
      setFavorites(favorites.filter(fav => fav.id !== track.id))
    } else {
      setFavorites([...favorites, track])
    }
  }

  if (!selectedMood) {
    return (
      <div className="min-h-screen bg-gray-50 p-8">
        <div className="max-w-4xl mx-auto text-center">
          <h2 className="text-3xl font-bold text-gray-800 mb-4">
            No mood selected
          </h2>
          <p className="text-gray-600">
            Please go to home page and select your mood
          </p>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gray-50 p-8">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-4xl font-bold text-gray-800 mb-2">
          {selectedMood.emoji} {selectedMood.mood} Playlist
        </h1>
        <p className="text-gray-600 mb-8">
          Perfect songs for your current mood
        </p>

        {loading && (
          <div className="text-center py-12">
            <div className="text-4xl mb-4 animate-pulse">ðŸŽµ</div>
            <p className="text-gray-600">Loading your playlist...</p>
          </div>
        )}

        {error && (
          <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-6">
            <p className="text-red-800">{error}</p>
            <button
              onClick={fetchTracks}
              className="mt-2 text-red-600 hover:text-red-800 underline"
            >
              Try again
            </button>
          </div>
        )}

        {!loading && !error && (
          <div className="space-y-4">
            {tracks.length === 0 ? (
              <p className="text-center text-gray-600">No tracks found</p>
            ) : (
              tracks.map((track) => (
                <TrackCard
                  key={track.id}
                  track={track}
                  onFavorite={handleFavorite}
                  isFavorite={favorites.some(fav => fav.id === track.id)}
                />
              ))
            )}
          </div>
        )}
      </div>
    </div>
  )
}

export default PlaylistPage
