import {getAccessToken} from './spotifyAuth'

export const getRecommendations = async (genres, limit = 20) => {
  try {
    const token = await getAccessToken()

    const response = await fetch(
      `https://api.spotify.com/v1/recommendations?seed_genres=${genres.join(',')}&limit=${limit}`,
      {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      }
    )

    // 如果是404，说明genre不支持
    if (response.status === 404) {
      console.error('Genre not supported:', genres)
      return []
    }

    // 检查响应是否成功
    if (!response.ok) {
      console.error('API Error:', response.status)
      return []
    }

    const data = await response.json()
    return data.tracks || []
  } catch (error) {
    console.error('Error:', error)
    return []
  }
}
