import React, {useState} from 'react'
import MoodCard from '../components/MoodCard'
import {useNavigate} from 'react-router-dom'

const HomePage = ({setSelectedMood}) => {
  const navigate = useNavigate()

  const moods = [
    {id: 1, mood: 'Happy', emoji: 'ðŸ˜Š', color: 'bg-yellow-400', genre: 'pop'},
    {id: 2, mood: 'Sad', emoji: 'ðŸ˜¢', color: 'bg-blue-400', genre: 'acoustic'},
    {id: 3, mood: 'Energetic', emoji: 'âš¡', color: 'bg-red-500', genre: 'rock'},
    {id: 4, mood: 'Chill', emoji: 'ðŸ˜Œ', color: 'bg-green-400', genre: 'chill'},
    {id: 5, mood: 'Romantic', emoji: 'ðŸ’•', color: 'bg-pink-400', genre: 'romance'},
    {id: 6, mood: 'Focus', emoji: 'ðŸŽ¯', color: 'bg-purple-500', genre: 'study'},
  ]

  const handleMoodClick = (mood) => {
    setSelectedMood(mood)
    navigate('/playlist')
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-blue-50 p-8">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-5xl font-bold text-gray-800 mb-4 text-center">
          How are you feeling today?
        </h1>
        <p className="text-xl text-gray-600 mb-12 text-center">
          Choose your mood and discover the perfect music
        </p>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {moods.map((mood) => (
            <MoodCard
              key={mood.id}
              mood={mood.mood}
              emoji={mood.emoji}
              color={mood.color}
              onClick={() => handleMoodClick(mood)}
            />
          ))}
        </div>
      </div>
    </div>
  )
}

export default HomePage
