import React from 'react'
import MoodCard from '../components/MoodCard'
import {useNavigate} from 'react-router-dom'

const HomePage = ({setSelectedMood}) => {
  const navigate = useNavigate()

  // 使用Spotify官方支持的genres
  const moods = [
    {
      id: 1,
      mood: 'Happy',

      color: 'bg-yellow-400',
      genres: ['pop']  // 简单可靠
    },
    {
      id: 2,
      mood: 'Sad',
      emoji: '😢',
      color: 'bg-blue-400',
      genres: ['acoustic']  // 简单可靠
    },
    {
      id: 3,
      mood: 'Energetic',
      emoji: '⚡',
      color: 'bg-red-500',
      genres: ['rock']  // 简单可靠
    },
    {
      id: 4,
      mood: 'Chill',
      emoji: '😌',
      color: 'bg-green-400',
      genres: ['ambient']  // 简单可靠
    },
    {
      id: 5,
      mood: 'Romantic',
      emoji: '💕',
      color: 'bg-pink-400',
      genres: ['guitar']  // 简单可靠
    },
    {
      id: 6,
      mood: 'Focus',
      emoji: '🎯',
      color: 'bg-purple-500',
      genres: ['classical']  // 简单可靠
    },
  ]

  const handleMoodClick = (mood) => {
    setSelectedMood(mood)
    navigate('/playlist')
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-blue-50 p-8">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-5xl font-bold text-gray-800 mb-4 text-center">
          How are you feeling today?
        </h1>
        <p className="text-xl text-gray-600 mb-12 text-center">
          Choose your mood and discover the perfect music from Spotify
        </p>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {moods.map((mood) => (
            <MoodCard
              key={mood.id}
              mood={mood.mood}
              emoji={mood.emoji}
              color={mood.color}
              onClick={() => handleMoodClick(mood)}
            />
          ))}
        </div>
      </div>
    </div>
  )
}

export default HomePage
