import React from 'react';

const AboutPage = () => {
  return (
    <div className="container mx-auto px-4 py-8">
      <p className="text-gray-800 text-lg leading-relaxed max-w-3xl mx-auto text-center">
        Welcome to Mood Meal Finder, it helps you discover delicious meals based on your current mood. 
        Simply choose how you're feeling from the available options, and the app will show you three 
        curated meal recommendations that match your mood. You can then view detailed recipes and 
        cooking instructions to explore new dishes and cooking ideas!
      </p>
    </div>
  );
};

export default AboutPage;
