import {getAccessToken} from './spotifyAuth'

// 获取推荐音乐
export const getRecommendations = async (genres, limit = 20) => {
  try {
    const token = await getAccessToken()

    const response = await fetch(
      `https://api.spotify.com/v1/recommendations?seed_genres=${genres.join(',')}&limit=${limit}`,
      {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      }
    )

    const data = await response.json()
    return data.tracks || []
  } catch (error) {
    console.error('Error:', error)
    return []
  }
}
