import {getAccessToken} from './spotifyAuth'

// Map moods to search queries
const MOOD_QUERIES = {
  pop: 'genre:pop',
  acoustic: 'genre:acoustic',
  rock: 'genre:rock',
  ambient: 'chill ambient relaxing',
  romance: 'love romantic',
  classical: 'genre:classical'
}

export const getRecommendations = async (genres, limit = 20) => {
  try {
    const token = await getAccessToken()
    const genreArray = Array.isArray(genres) ? genres : [genres]
    const primaryGenre = genreArray[0]

    // Use search instead of recommendations
    const query = MOOD_QUERIES[primaryGenre] || primaryGenre
    const url = `https://api.spotify.com/v1/search?q=${encodeURIComponent(query)}&type=track&limit=${limit}`

    console.log('Searching Spotify:', query)

    const response = await fetch(url, {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    })

    if (!response.ok) {
      const errorText = await response.text()
      console.error('Search failed:', response.status, errorText)
      return []
    }

    const data = await response.json()

    // Return tracks with full Spotify structure (no transformation needed)
    const tracks = data.tracks.items

    console.log(`✓ Found ${tracks.length} tracks`)
    return tracks

  } catch (error) {
    console.error('Error in getRecommendations:', error)
    return []
  }
}
